Method.Implementation:
#dim sc As %Status = $$$OK if (##class(%Dictionary.PropertyDefinition).IDKEYExists(className, name)) { return $$$ERROR($$$GeneralError, "Property " _ name _ " already exists") }
set class = ##class(%Dictionary.ClassDefinition).%OpenId(className, ,.sc) return:$$$ISERR(sc) sc
set property = ##class(%Dictionary.PropertyDefinition).%New() set property.Name = name set property.Type = type set property.Collection = collection set property.Required = required if (($get(displayName)'="") && ($$$comClassKeyGet(className,$$$cCLASSpropertyclass)[$$$propClass)) { set sc = property.Parameters.SetAt(displayName, $$$propDisplayNameParam) return:$$$ISERR(sc) sc }
set sc = class.Properties.Insert(property) return:$$$ISERR(sc) sc
set sc = class.%Save() return:$$$ISERR(sc) sc
kill class return ..compileClass(className)

#dim sc As %Status = $$$OK
if ('##class(%Dictionary.PropertyDefinition).IDKEYExists(className, name)) {
	return $$$ERROR($$$GeneralError, "Property " _ name _ " does not exist")
}

set sc = ##class(%Dictionary.PropertyDefinition).IDKEYDelete(className, name)
return:$$$ISERR(sc) sc

return ..compileClass(className)
